import React from "react";
import styled, { keyframes } from "styled-components";
import { Link } from "react-router-dom";
import BrandLogo from "../components/BrandLogo";

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const HomeContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)),
    url("/images/background/traditional-tea.jpg");
  background-size: cover;
  background-position: center;
  background-attachment: fixed;
  color: white;
`;

const HeroSection = styled.section`
  min-height: 100vh;
  width: 100vw;
  margin-left: calc(-50vw + 50%);
  margin-right: calc(-50vw + 50%);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: var(--primary-color);
  color: white;
  padding: 2rem;
  text-align: center;
  max-width: 1200px;
  margin: 0 auto;
  animation: ${fadeIn} 1s ease-out;
  background-color: #ff7b28;
`;

const Title = styled.h1`
  font-size: 3.5rem;
  margin-bottom: 1rem;
  font-weight: 700;
`;

const Subtitle = styled.p`
  font-size: 1.2rem;
  margin-bottom: 2rem;
  line-height: 1.6;
  max-width: 600px;
`;

const MenuButton = styled(Link)`
  background-color: white;
  color: var(--primary-color);
  padding: 1rem 2rem;
  border-radius: 8px;
  text-decoration: none;
  font-weight: 600;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #f0f0f0;
  }
`;

const SectionTitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 3rem;
  text-align: center;
  font-family: "Hahmlet", serif;
  color: #ff7b28;
`;

const EventSection = styled.div`
  padding: 6rem 2rem;
  background: rgba(0, 0, 0, 0.8);
  backdrop-filter: blur(10px);
`;

const EventGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const EventCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  overflow: hidden;
  animation: ${fadeIn} 1s ease-out;
  backdrop-filter: blur(10px);
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-10px);
  }

  img {
    width: 100%;
    height: 250px;
    object-fit: cover;
  }

  .content {
    padding: 2rem;

    h3 {
      font-size: 1.5rem;
      margin-bottom: 1rem;
      font-family: "Hahmlet", serif;
    }

    p {
      opacity: 0.9;
      margin-bottom: 1rem;
      line-height: 1.6;
    }

    .date {
      font-size: 0.9rem;
      color: #ff7b28;
      font-weight: 600;
    }
  }
`;

const RecommendSection = styled.div`
  padding: 6rem 2rem;
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(10px);
`;

const FeaturedSection = styled.div`
  padding: 6rem 2rem;
  background: rgba(0, 0, 0, 0.8);
`;

const FeaturedGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const FeaturedItem = styled.div`
  background: rgba(255, 255, 255, 0.1);
  padding: 2.5rem;
  border-radius: 20px;
  text-align: center;
  backdrop-filter: blur(10px);
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-5px);
  }

  h3 {
    font-size: 1.5rem;
    margin-bottom: 1.5rem;
    font-family: "Hahmlet", serif;
    color: #ff7b28;
  }

  p {
    opacity: 0.9;
    line-height: 1.8;
  }
`;

function Home() {
  return (
    <HomeContainer>
      <HeroSection>
        <BrandLogo />
        <Title>K.Dot Cafe</Title>
        <Subtitle>
          전통차의 깊이와 현대적 감각이 만나는 곳<br />
          한국의 전통 음료를 새롭게 재해석한 특별한 경험
        </Subtitle>
        <MenuButton to="/menu">메뉴 보기</MenuButton>
      </HeroSection>

      <EventSection>
        <SectionTitle>진행중인 이벤트</SectionTitle>
        <EventGrid>
          <EventCard>
